
// task(timerCallback())
// callback(cost_time) in ms
var timing = function(task, callback){
    var timerStop = function(){
        var d2 = new Date();    
        var time1 = d1.getTime();
        var time2 = d2.getTime();
        var cost_time = time2 - time1;
        if(callback && typeof callback === 'function'){
            callback(cost_time);
        }
    };

    var d1 = new Date();
    task(timerStop);
};

